import React from 'react';
import Card from './components/Card';
import Form from './components/Form';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      cardName: '',
      cardDescription: '',
      cardAttr1: '',
      cardAttr2: '',
      cardAttr3: '',
      cardImage: '',
      cardRare: 'normal',
      cardTrunfo: false,
      hasTrunfo: false,
      isSaveButtonDisabled: true,
      allCards: [],
    };
  }

  handlechange = ({ target: { name, value, checked, type } }) => {
    const newValue = type === 'checkbox' ? checked : value;
    if (['cardAttr1', 'cardAttr2', 'cardAttr3'].includes(name)) {
      return this.setNewValue({ name, value: parseInt(newValue, 10) });
    }
    return this.setNewValue({ name, value: newValue });
  }

  onSaveButtonClick =() => {
    const { ...newCard } = { ...this.state };
    delete newCard.hasTrunfo;
    delete newCard.isSaveButtonDisabled;
    newCard.allCards.push(newCard);
    const updatedCardDeck = newCard.allCards;
    delete newCard.allCards;
    this.setState({ allCards: updatedCardDeck }, this.resetState());
  }

  render() {
    const { cardName, cardDescription, cardAttr1, cardAttr2, cardAttr3,
      cardImage, cardRare, cardTrunfo, hasTrunfo, isSaveButtonDisabled } = this.state;
    return (
      <div>
        <Form
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          hasTrunfo={ hasTrunfo }
          isSaveButtonDisabled={ isSaveButtonDisabled }
          onInputChange={ this.handlechange }
          onSaveButtonClick={ () => {} }
        />

        <Card
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
        />
      </div>
    );
  }
}

export default App;
